% lat            = latitude (deg)

% R2    = 0.84691
% RMSE  = 14.0683
% MEAN  = 108.9265
% RANGE = 31.1172 - 179.2732

beta( 1 )= 6.3153e-01 ;
beta( 2 )= 5.9864e-01 ;
beta( 3 )= 5.6972e-01 ;
