beta( 5 )= -9.9611e+00 ;
beta( 6 )= -3.3596e-01 ;

v=beta(1).*w.^beta(2).*r35.^beta(3).*(1-beta(4).*cos((phi-beta(5))*pi/180)).*vmax.^beta(6);
